
var exp = module.exports;

var Tower = function(){
  this.ids = {};
  this.idsArr = null;
}

var pro = Tower.prototype;

pro.add = function(id){
  this.ids[id] = id;
  
  if(!!this.idsArr)
    this.idsArr.push(id);
}

pro.remove = function(id){
  if(!!this.ids[id])
    delete this.ids[id];
  
  this.idsArr = null;
}

pro.getIds = function(){
  return this.ids;
}

pro.getIdsArr = function(){
  if(!this.idsArr){
    this.idsArr = [];
    for(var id in ids)
      this.idsArr.push(id);
  }
  
  return this.idsArr;  
}

exp.create = function(){
  return new Tower();
}

